import React, { useState, useEffect } from 'react';
import ROSLIB from 'roslib';
import './App.css';

function App() {
  const [recording, setRecording] = useState(false);
  const [currentCam, setCurrentCam] = useState(0);
  const [imageSrc, setImageSrc] = useState('');
  const [ros, setRos] = useState(null);
  const cams = [
    '/camera_image/cam_VL',
    '/camera_image/cam_VR',
    '/camera_image/cam_MR',
    '/camera_image/cam_HR',
    '/camera_image/cam_HL',
    '/camera_image/cam_ML'
  ];

  // Establish WebSocket connection
  useEffect(() => {
    const newRos = new ROSLIB.Ros({
      url: 'ws://192.168.26.4:9090'
    });

    newRos.on('connection', () => {
      console.log('Connected to websocket server.');
    });

    newRos.on('error', (error) => {
      console.log('Error connecting to websocket server: ', error);
    });

    newRos.on('close', () => {
      console.log('Connection to websocket server closed.');
    });

    setRos(newRos);

    // Clean up function
    return () => {
      newRos.close();
    };
  }, []);

  // Handle subscribing to the current camera topic
  useEffect(() => {
    if (!ros) return;

    const listener = new ROSLIB.Topic({
      ros,
      name: cams[currentCam],
      messageType: 'sensor_msgs/Image'
    });

listener.subscribe((message) => {
  const imageData = message.data;
  const width = message.width;
  const height = message.height;
  const canvas = document.createElement('canvas');
  const context = canvas.getContext('2d');
  canvas.width = width;
  canvas.height = height;
  const imageDataObj = context.createImageData(width, height);

  for (let i = 0; i < height * width; i++) {
    imageDataObj.data[i * 4 + 0] = imageData[i * 3 + 2]; // Blue
    imageDataObj.data[i * 4 + 1] = imageData[i * 3 + 1]; // Green
    imageDataObj.data[i * 4 + 2] = imageData[i * 3 + 0]; // Red
    imageDataObj.data[i * 4 + 3] = 255; // Alpha
  }

  context.putImageData(imageDataObj, 0, 0);
  setImageSrc(canvas.toDataURL());
});


    return () => {
      listener.unsubscribe();
    };
  }, [currentCam, ros]);

  const toggleRecording = () => {
    setRecording(!recording);
    fetch(`http://192.168.26.4:5000/${recording ? 'stop' : 'start'}`, {
      method: 'POST'
    });
  };

  const changeCamera = (direction) => {
    setCurrentCam((prev) => (prev + direction + cams.length) % cams.length);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>{cams[currentCam]}</h2>
        <div>
          <button onClick={() => changeCamera(-1)}>&lt;</button>
          <img src={imageSrc} alt="Camera feed" />
          <button onClick={() => changeCamera(1)}>&gt;</button>
        </div>
        <button onClick={toggleRecording}>
          {recording ? 'Stop Recording' : 'Start Recording'}
        </button>
      </header>
    </div>
  );
}

export default App;
