import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [recording, setRecording] = useState(false);
  const [diskSpace, setDiskSpace] = useState({ total: 0, used: 0, free: 0 });
  const [cameraFeeds, setCameraFeeds] = useState([
    { id: 1, ip: '192.168.26.70' },
    { id: 2, ip: '192.168.26.71' },
    { id: 3, ip: '192.168.26.72' },
    { id: 4, ip: '192.168.26.73' },
    { id: 5, ip: '192.168.26.74' },
    { id: 6, ip: '192.168.26.75' }
  ]);

  const toggleRecording = () => {
    const action = recording ? 'stop' : 'start';
    fetch(`http://192.168.26.4:5000/${action}`, { method: 'POST' })
      .then(() => setRecording(!recording));
  };

  useEffect(() => {
    fetchDiskSpace();
    const intervalId = setInterval(fetchDiskSpace, 10000);
    return () => clearInterval(intervalId);
  }, []);

  const fetchDiskSpace = () => {
    fetch('http://192.168.26.4:5000/disk-space')
      .then(response => response.json())
      .then(data => setDiskSpace(data))
      .catch(error => console.error('Error fetching disk space:', error));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ROS Image Viewer</h1>
        <button onClick={toggleRecording}>
          {recording ? 'Stop Recording' : 'Start Recording'}
        </button>
        <div className="disk-space-info">
          <p>Total: {diskSpace.total} GB</p>
          <p>Used: {diskSpace.used} GB</p>
          <p>Free: {diskSpace.free} GB</p>
        </div>
      </header>
      <div className="camera-grid">
        {cameraFeeds.map(feed => (
          <div key={feed.id} className="camera-feed">
            <img src={`http://${feed.ip}:81/`} alt={`Camera ${feed.id}`} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
